import styled from "styled-components";

export const DivCesta = styled.div`
    width: 305px;
    height: 70px;
    border: 0.2rem solid black;
    display: flex;
    align-items: center;
`
export const ImgProduto = styled.img`
    height: 68px;
    width: 68px;
    border: 0.1rem solid black;
`
export const DivDescProduto = styled.div`
    height: 68px;
    width: auto;
    border: 0.1rem solid black;
`
export const PTituloProduto = styled.p`
    font-weight: bold;
    font-family: 'Poppins', sans-serif;
    font-size: 12px;
`
export const PValorProduto = styled.p`
    font-family: 'Poppins', sans-serif;
    font-size: 10px;
`
export const PValorDescontoProduto = styled.p`
    font-weight: bold;
    font-family: 'Poppins', sans-serif;
    font-size: 12px;
`
